import { useRef } from "react";

import Card from "../ui/Card";
import LoadingSpinner from "../ui/LoadingSpinner";
import classes from "./QuoteForm.module.css";

const QuoteForm = (props) => {
  const authorChangeHandler = useRef();
  const TextChangeHandler = useRef();

  const enteredAuthor = authorChangeHandler.target.value();
  const enteredText = authorChangeHandler.target.value();

  const submitFormHandler = (event) => {
    event.preventDefault();

    // optional: Could validate here

    props.onAddQuote({ author: enteredAuthor, text: enteredText });
  };

  return (
    <Card>
      <form className={classes.form} onSubmit={submitFormHandler}>
        {props.isLoading && (
          <div className={classes.loading}>
            <LoadingSpinner />
          </div>
        )}

        <div className={classes.control}>
          <label htmlFor="author">Author</label>
          <input type="text" id="author" ref={authorChangeHandler} />
        </div>
        <div className={classes.control}>
          <label htmlFor="text">Text</label>
          <textarea id="text" rows="5" ref={TextChangeHandler}></textarea>
        </div>
        <div className={classes.actions}>
          <button className="btn">Add Quote</button>
        </div>
      </form>
    </Card>
  );
};

export default QuoteForm;
